import { useState } from 'react';

// Это разминочный вопрос, который поможет тебе
// познакомиться с редактором.
// Большая часть кода уже заполнена за тебя.
export default function App() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <button
        onClick={() => {
          // Исправь ошибку в следующей строке, 
          // чтобы завершить задание.
          setCount(count - 1);
        }}>
        Clicks: {count}
      </button>
    </div>
  );
}
